mapper.select:
SELECT id, order_no, sku, skc, custom_sku, created_at, shop_id, alias_name, product_title, product_properties, product_img_url, draw_user_name, produce_user_name, ship_user_name, repeat_reason FROM temu_order_return
AND order_no = #{reqVO.orderNo}
AND repeat_reason = #{reqVO.repeatReason}
AND created_at BETWEEN #{reqVO.createdAt[0]} AND #{reqVO.createdAt[1]}
ORDER BY created_at DESC LIMIT #{reqVO.pageSize} OFFSET #{reqVO.offset}

SELECT COUNT(1)
FROM temu_order_return
